import"./hoisted.CjCQvWA7.js";const t=document.getElementById("loginForm");t.addEventListener("submit",async function(o){o.preventDefault();const n=new FormData(t),a=Object.fromEntries(n);try{const e=await fetch("http://localhost:3000/login",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(a)});if(!e.ok){alert("Erreur lors de la connexion");return}const r=await e.json();r.redirect?window.location.href=r.redirect:alert("Erreur : "+r.message)}catch(e){console.error("Erreur:",e),alert("Une erreur est survenue")}});
